import Listhead from "./components/ListHead";
import Listcontent from "./components/Listcontent" // Corrected the import statement for ListItem
import React, { useEffect, useState } from 'react';

const App = () => {
  const userEmail = 'ni124us@gmail.com';
  const [tasks, setTasks] = useState(null);

  const getdata = async () => {
    try {
      const response = await fetch(`http://localhost:8000/to/${userEmail}`);
      const json = await response.json();
      console.log(json);
      setTasks(json);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getdata();
  }, []); // Empty dependency array ensures useEffect runs only once on component mount

  // Sorting tasks by date
  const sortedTasks = tasks && [...tasks].sort((a, b) => new Date(a.date) - new Date(b.date));

  return (
    <div className="app">
      <ListHead listname={'🏝️ HOLIDAY TICK LIST'} /> {/* Added a space after emoji for readability */}
      {sortedTasks && sortedTasks.map((task) => <Listcontent key={task.id} task={task} />)}
    </div>
  );
}

export default App;
