import React, { useEffect, useState } from 'react';
import ListHead from './components/ListHead';
import Listcontent from './components/Listcontent';
import Auth from './components/Auth'; // Ensure correct path and filename for Auth component
import { useCookies } from 'react-cookie';

const App = () => {
  const [cookies, setCookies, removeCookies] = useCookies(['userEmail', 'AuthToken']);
  const userEmail = cookies.userEmail;
  const authToken = cookies.AuthToken;
  const [tasks, setTasks] = useState(null);

  useEffect(() => {
    const getdata = async () => {
      try {
        if (authToken && userEmail) {
          const response = await fetch(`${process.env.REACT_APP_SERVERURL}/to/${userEmail}`);
          if (response.ok) {
            const json = await response.json();
            setTasks(json);
          } else {
            console.error('Failed to fetch data:', response.status);
            // Handle error here if needed
          }
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        // Handle error here if needed
      }
    };

    getdata();
  }, [authToken, userEmail]); // Fetch data when authToken or userEmail changes

  // Sorting tasks by date
  const sortedTasks = tasks && [...tasks].sort((a, b) => new Date(a.date) - new Date(b.date));

  return (
    <div className="app">
      {!authToken && <Auth />}
      {authToken && (
        <>
          <ListHead listname={'ðŸï¸ HOLIDAY TICK LIST'} getData={getdata} /> {/* Pass getData function as prop */}
          <p className="user-email">Welcome Back {userEmail}</p>
          {sortedTasks && sortedTasks.map((task) => <Listcontent key={task.id} task={task} getData={getdata} />)}
        </>
      )}
      <p className="copyright">Â© Creative Coding LLC</p>
    </div>
  );
};

export default App;
