import React, { useState } from "react";

const Module = ({ mode, setShowModule, task }) => {
  const editMode = mode === 'edit'; // Determine edit mode based on prop 'mode'
  
  const [data, setData] = useState({
    user_email: editMode ? task.user_email : "",
    title: editMode ? task.title : "",
    progress: editMode ? task.progress : 50,
    date: editMode ? "" : new Date()
  });

  const postData=()=>
{
  try{
    fetch('http://localhost:8000/to')
  }catch(err){
    console.log(err)
  }
}
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <div className="overlay">
      <div className="modal">
        <div className="form-title-container">
          <h3>let's {mode} your task</h3>
          <button onClick={() => setShowModule(false)}>X</button>
        </div>
        <form>
          <input
            required
            maxLength={30}
            placeholder="Your task goes here"
            name="title"
            value={data.title}
            onChange={handleChange}
          />
          <br/>
          <label htmlFor="range">Drag to select your current progress</label>
          <input 
            required
            type="range"
            id="range"
            min="0"
            max="100"
            name="progress"
            value={data.progress}
            onChange={handleChange}
          />
          <input className={mode} type="submit"/>
        </form>
      </div>
    </div>
  );
};

export default Module;
